import { useState } from "react";

const Greeting = () => {
  const [hello, setHello] = useState("Hell9iiio");

  const handleGreeting = () => {
    setHello["hello"];
  };

  //if the mouse hovers over it, then show 'setHello', if not show 'hello'

  return (
    <div>
      <h1
        className="animate__animated animate__bounce animate__pulse"
        onMouseOver={handleGreeting()}
      >
        {hello}
      </h1>
      <p>lorem ipsum ...</p>
    </div>
  );
};

export default Greeting;
