import React from "react";
import { useState } from "react";

const MelodyGenerator = (props) => {
  const [closePopup, setClosePopup] = useState(true);

  return props.trigger ? (
    <div className="melody-generator">
      <div className="melody-generator-inner">
        <button onClick={() => setClosePopup(true)} className="close-btn">
          CLOSE
        </button>
        <p>
          this is the content of the melody generator popup window, description
          of the project etc.{" "}
        </p>
        {props.children}
      </div>
    </div>
  ) : (
    ""
  );
};

export default MelodyGenerator;
