import React from 'react'
import { useState } from 'react'; 

export default function Content() {

  const [name, setName] = useState('')

  const [count, setCount] = useState(0); 

  const handleGreeting = () => {
    const nameGreeting = ['FOBBLE-SWITCH', 'GLOOBER-GAP', 'QUASTER-FIZER']
    const int = Math.floor(Math.random() * 3)
    setName(nameGreeting[int]); 
  }

  const handleClick = () => { 
    setCount(count + 1)
     console.log(count)
  }

  const handleClick2 = (name) => { 
     console.log(`${name} was clicked`)
  }

  const handleClick3 = (e) => { 
     console.log(e.target.innerText)
  }

  return (
    <div>
        <p onDoubleClick={handleClick}>
          Good day to you, {setCount(count + 1)}  
        </p>
        <p>You've clicked {setCount} times!</p>

        <button onClick={handleGreeting}>choose name</button>
        <button onClick={handleClick}>Count click</button>
        <button onClick={(e) => handleClick3(e)}>Click it</button>
    </div>
  )
}
