import { useState } from "react";
import Photo from "./Photo.js";

const About = () => {
  const [hello, setHello] = useState("Hello");

  const handleGreeting = () => {
    setHello("Bitch");
  };

  const handleMouseLeave = () => {
    setHello("Hello");
  };

  return (
    <div className="about-container">
      <div className="profile-text-container">
        <h1
          className="animate__animated animate__bounce animate__pulse"
          onMouseOver={handleGreeting}
          onMouseLeave={handleMouseLeave}
        >
          {hello}
        </h1>
        <p>Hey, I'm Marek! Welcome to my portfolio website! </p>
        <p>
          I'm a frontend web developer and musician with an eye for minimalist
          elegance and playful design. I'm passionate about building unique and
          creative websites and apps, especially for artists. As a newly baked
          web developer, I'm still learning - deepening my knowledge - and
          continously integrate the newest tech into my projects.
        </p>
      </div>

      <div className="photo-container">
        <Photo />
      </div>
    </div>
  );
};

export default About;
