import React, { useState } from "react";
import Synth from "/Users/marekpolgesek/Desktop/{coding, dev}/Frontend Projects/Portfolio/portfolio-website/src/Images/GayestSynth.png";
import "./Portfolio.css";
import "animate.css";
import MelodyGeneratorScr from "../Images/MelodyGenerator.png";

const MelodyGenerator = () => {
  const [modal, setModal] = useState(false);

  const toggleModal = () => {
    setModal(!modal);
  };

  const [className, setClassName] = useState(false);

  const handleHover = () => {
    setClassName((className) => !className);
  };

  let toggleClassCheck = className
    ? " animate__animated animate__wobble"
    : null;

  return (
    <>
      <main>
        <div className="image-container">
          <figure>
            <img
              src={Synth}
              onClick={toggleModal}
              className={`img${toggleClassCheck}`}
              onMouseOver={handleHover}
            ></img>
            <figcaption>
              <h1>Melody Generator</h1>
              <h3>Webdesign & Webdevelopment</h3>
            </figcaption>
          </figure>
        </div>
      </main>
      {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay">
            <div className="modal-content">
              <div></div>
              <img src={MelodyGeneratorScr} className="screenshot"></img>
              <div className="project-description">
                <h1>Melody Generator</h1>
                <p>
                  Music is one of my biggest passions, so I naturally gravitate
                  towards apps and sites for and about music. This web app lets
                  you<mark> generate a random melody</mark> based on a scale of
                  your choice. The goal is to implement music theory to generate
                  more musical and appealing results. The{" "}
                  <mark>biggest challenge</mark> here was to learn about
                  <mark>
                    {" "}
                    APIs and how to implement them in vanilla Javascript
                  </mark>
                  . It was overwhelming to keep an overview of all the elements
                  and their nesting, given that f.e. every note has a unique
                  frequency and needs to be embedded correctly.
                  <mark>
                    {" "}
                    Now I know how crucial clean and transparent code is!
                  </mark>{" "}
                  Next time I would use React to build a similar project. I’ve
                  used <mark>Bootstrap</mark> to streamline and facilitate
                  styling. Now I’m familiar with the gist of the documentation
                  and know what kind of project(s) I would use it for.
                </p>
                <div className="used-tech">
                  <p>Tech used: </p>
                  <ul>
                    <li>HTML, CSS, Javascript</li>
                    <li>CSS Bootstrap</li>
                    <li>Music API (check which one) </li>
                  </ul>
                </div>
              </div>
              <button className="close-modal" onClick={toggleModal}>
                close me
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default MelodyGenerator;
