import MelodyGenerator from "./MelodyGenerator.js";
import Popup from "reactjs-popup";
import "reactjs-popup/dist/index.css";
import Synth from "/Users/marekpolgesek/Desktop/{coding, dev}/Frontend Projects/Portfolio/portfolio-website/src/Images/GayestSynth.png";
import SynthKeyD1 from "/Users/marekpolgesek/Desktop/{coding, dev}/Frontend Projects/Portfolio/portfolio-website/src/Images/Key-D1.png";

const GraphicOne = () => {
  const handleClick = () => {
    <div className="popup-window" id="pop-up-code">
      <Popup
        trigger={<button className="button"> Open Modal </button>}
        modal
        nested
      >
        {(close) => (
          <div className="modal">
            <button className="close" onClick={close}>
              &times;
            </button>
            <div className="header"> Melody Generator </div>
            <div className="content">
              {" "}
              <MelodyGenerator />
              <br />
              Lorem ipsum dolor sit amet, consectetur adipisicing elit.
              Consequatur sit commodi beatae optio voluptatum sed eius cumque,
              delectus saepe repudiandae explicabo nemo nam libero ad,
              doloribus, voluptas rem alias. Vitae?
            </div>
            <div className="actions">
              <Popup
                trigger={<button className="button"> Trigger </button>}
                position="top center"
                nested
              >
                <span>
                  Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                  Beatae magni omnis delectus nemo, maxime molestiae dolorem
                  numquam mollitia, voluptate ea, accusamus excepturi deleniti
                  ratione sapiente! Laudantium, aperiam doloribus. Odit, aut.
                </span>
              </Popup>
              <button
                className="button"
                onClick={() => {
                  console.log("modal closed ");
                  close();
                }}
              >
                close modal
              </button>
            </div>
          </div>
        )}
      </Popup>
    </div>;
  };

  return (
    <div>
      <div className="synth-container">
        <img src={Synth} className="synthesizer" onClick={handleClick}></img>
      </div>

      <div>
        <img src={SynthKeyD1} className="synth-key-d-one"></img>
      </div>

      <div>
        <MelodyGenerator />
      </div>
    </div>
  );
};

export default GraphicOne;
