import React, { useState } from "react";
import Synth from "/Users/marekpolgesek/Desktop/{coding, dev}/Frontend Projects/Portfolio/portfolio-website/src/Images/GayestSynth.png";
import "./Portfolio.css";
import "animate.css";
import NanseaScreenshot from "../Images/NanseaWebsite-screenshot.png";
import { IoIosCloseCircleOutline } from "react-icons/io";

const NanseaWebsite = () => {
  const [modal, setModal] = useState(false);

  const toggleModal = () => {
    setModal(!modal), console.log("toggled");
  };

  const [className, setClassName] = useState(false);

  const handleHover = () => {
    setClassName((className) => !className);
  };

  let toggleClassCheck = className
    ? " animate__animated animate__wobble"
    : null;

  return (
    <>
      <main className="project-container">
        <div className="image-container">
          <figure>
            <img
              src={Synth}
              onClick={toggleModal}
              className={`img${toggleClassCheck}`}
              onMouseOver={handleHover}
            ></img>
            <figcaption>
              <h1>Nansea </h1>
              <h3>Webdesign & Webdevelopment</h3>
            </figcaption>
          </figure>
        </div>
      </main>
      {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay">
            <div className="modal-content">
              <div></div>
              <img src={NanseaScreenshot} className="screenshot"></img>
              <div className="project-description">
                <h1>Nansea</h1>
                <p>
                  For my first{" "}
                  <mark>
                    <b>React</b>
                  </mark>{" "}
                  project, I’ve decided to build myself an artist homepage.
                  Starting from scratch, using websites of some of my favourite
                  artists’ as inspo, I aimed a clean and minimalist feel. I
                  wanted to wrap my artist portfolio in a calm and appealing
                  design. The biggest<mark>challenge </mark> was the{" "}
                  <mark>
                    responsive hamburger menu and its dropdown behaviour
                  </mark>
                  , which I'm now happy with. <br></br>In order to host this
                  project, I had to dive into the backend of developing. This
                  way, I’ve acquired{" "}
                  <mark>basic knowledge of PhP and mySQL</mark>. I believe I’ve
                  achieved a fully responsive, smooth design and user
                  experience.
                </p>{" "}
                <br></br>
                <div className="used-tech">
                  <p>Tech used: </p>
                  <ul>
                    <li>React</li>
                    <li>framer motion</li>
                    <li>PhP</li>
                    <li>SQL</li>
                  </ul>
                </div>
              </div>
              <button className="close-modal" onClick={toggleModal}>
                close me
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default NanseaWebsite;
