import { useState } from "react";
import Photo from "./Photo.js";

const About = () => {
  const [hello, setHello] = useState("Hello");

  const handleGreeting = () => {
    setHello("THIS IS THE ABOUT PAGE BIIITIITICIHICHIHCICHICHIH");
  };

  const handleMouseLeave = () => {
    setHello("Enjoy");
  };

  return (
    <div className="description-container">
      <div className="greeting-container">
        <h1
          className="animate__animated animate__bounce animate__pulse"
          onMouseOver={handleGreeting}
          onMouseLeave={handleMouseLeave}
        >
          {hello}
        </h1>
        <p>Hey, I'm Marek! Welcome to my portfolio website! </p>
        <p>
          I'm a freshly baked junior web developer excited to take on challening
          and creative projects. I'm especially interested in responsive design
          and web site behaviour
        </p>

        <div className="photo-container">
          <Photo />
        </div>
      </div>
    </div>
  );
};

export default About;
