import React from 'react'
import { useState } from 'react'; 

export default function Content() {

  const [name, setName] = useState('Marek')

  const handleGreeting = () => {
    const nameGreeting = ['Breadcrumb', 'Supersponge', 'Jellyfoe']
    const int = Math.floor(Math.random() * 3)
    setName(nameGreeting[int]); 
  }

  const handleClick = () => { 
     console.log('you clicked it, girl')
  }

  const handleClick2 = (name) => { 
     console.log(`${name} was clicked`)
  }

  const handleClick3 = (e) => { 
     console.log(e.target.innerText)
  }

  return (
    <div>
        <p onDoubleClick={handleClick}>
          Good day to you, {handleGreeting}  
        </p>
        <button onClick={handleClick}>Click it</button>
        <button onClick={() => handleClick2('Marek')}>Click it</button>
        <button onClick={(e) => handleClick3(e)}>Click it</button>
    </div>
  )
}
